import React from 'react'
import ReactDOM from 'react-dom'
import { API_KEY } from './API_KEY'
import YTSearch from 'youtube-api-search'

import SearchBar from './components/search_bar'

// "Downwards Data Flow" - because index.js is the most parent component, it is
// responsible for fetching data (so that it can be passed downstream)
YTSearch({key: API_KEY, term: 'surfboards'}, function (data) {
	console.log(data)
});

// Class-based component
class App extends Component {
	render() {
		return (
			<div>
				<SearchBar/>
			</div>
		)
	}
}

// Take the HTML generated by the component and put it on the page DOM
// reactDOM library modifies the DOM
ReactDOM.render(<App />, document.querySelector('.container'));